    fun guardaDatos() {

        val progressDialog = ProgressDialog(
            this,
            R.style.Theme_AppCompat_Light_Dialog
        )
        progressDialog.isIndeterminate = true
        progressDialog.setMessage("Cargando datos...")
        progressDialog.show()
        val motivo = motivotext.text.toString()
        val preferencias = this.getSharedPreferences("variables", Context.MODE_PRIVATE)
        val pku = preferencias.getString("pk", "0")!!
        val datos = JSONObject()
        try {
            datos.put("PK_GUIA", pk.toInt())
            datos.put("MOTIVO", motivo)
            datos.put("PK_USUARIO", pku.toInt())

        } catch (e: JSONException) {
            e.printStackTrace()
        }
        val requstQueue = Volley.newRequestQueue(this)
        val jsonObjectRequest: JsonObjectRequest = object : JsonObjectRequest(
            Method.POST, "https://appis.atah.online/api/Guias2/ReimprimirGuia", datos,
            Response.Listener { response ->

                try {

                    progressDialog?.dismiss()
                    val result = response["resultado"] as Int





                    if (result == 1) {
                        val costos = response.getJSONObject("data")



                        FECHA=costos.getString("fecha")
                            LINEA=costos.getString("linea")
                            FOLIO=costos.getString("folio")
                            AUTOBUS=costos.getString("autobus")
                            VALIDADOR=costos.getString("validador")
                            ORIGEN=costos.getString("origen")
                            DESTINO=costos.getString("destino")
                            BOLETOS=costos.getString("boletos")
                            IMPORTE=costos.getString("importe")
                            TT=costos.getString("tturno")
                            TS=costos.getString("tsalida")
                            TP=costos.getString("tpaso")
                            GASTOS=costos.getString("anticipo")
                            TOTAL=costos.getString("total")
                            IVA=costos.getString("iva")

imprimirguia()
                    } else {
                        val error = response.getString("mensaje")
                        Toast.makeText(this, error, Toast.LENGTH_SHORT).show()

                    }
                } catch (e: JSONException) {
                    e.printStackTrace()
                }
            },
            Response.ErrorListener { error ->
                progressDialog?.dismiss()
                Log.e("Rest Response", error.toString())
            }
        ) { //here I want to post data to sever
        }
        val MY_SOCKET_TIMEOUT_MS = 15000
        val maxRetries = 2
        jsonObjectRequest.retryPolicy = DefaultRetryPolicy(
            MY_SOCKET_TIMEOUT_MS,
            maxRetries,
            DefaultRetryPolicy.DEFAULT_BACKOFF_MULT
        )
        requstQueue.add(jsonObjectRequest)
    }

